import React from 'react';
import './App.scss';
import StarshipsList from '../StarshipsList/StarshipsList';

const ENDPOINT = 'https://swapi.co/api';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
        currentURL: ENDPOINT + '/starships/?page=1',
        starships: [],
        next: '',
        previous: '',
    }
  }

  componentDidMount(){
    this.getStarshipsData(this.state.currentURL);
  }

  getStarshipsData(currentURL) {
    fetch(currentURL)
      .then(data => data.json())
      .then(() => this.setState((data) => {
        return {
          currentURL: data,
          starships: data.results,
          next: data.next,
          previous: data.previous
        }
      }));
  }

  getPreviousPage() {
    this.getStarshipsData(currentURL);
  }

  getNextPage() {
    this.getStarshipsData(currentURL);
  }

  render() {
    return (
      <div className="App">
        <StarshipsList 
          starships={this.state.starships}
        />
        <button onClick={this.getPreviousPage} id='previous'>Previous</button>
        <button onClick={this.getNextPage} id='next'>Next</button>
      </div>
    );
  }
}

export default App
