import React from 'react';
import './App.scss';
import StarshipsList from '../StarshipsList/StarshipsList';

const ENDPOINT = 'https://swapi.co/api';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
        starships: [],
        next: '',
        previous: '',
    }
  }

  componentDidMount(){
    this.getStarshipsData();
  }

  getStarshipsData() {
    fetch(ENDPOINT + '/starships/?page=1')
      .then(data => data.json())
      .then(data => this.setState({
        starships: data.results,
        next: data.next,
        previous: data.previous
      }));
  }

  getPreviousPage() {
    if (this.state.previous === null) {
      return;
    }
    fetch(this.state.previous)
      .then(data => data.json())
      .then(data => this.setState({
        starships: data.results,
        next: data.next,
        previous: data.previous
      }));
  }

  getNextPage() {
    if (this.state.next === null) {
      return;
    }
    fetch(this.state.next)
      .then(data => data.json())
      .then(data => this.setState({
        starships: data.results,
        next: data.next,
        previous: data.previous
      }));
  }

  render() {
    return (
      <div className="App">
        <StarshipsList 
          starships={this.state.starships}
        />
        <button onClick={this.getPreviousPage} id='previous'>Previous</button>
        <button onClick={this.getNextPage} id='next'>Next</button>
      </div>
    );
  }
}

export default App
