  //       starships: data.results,
  //       next: data.next,
  //       previous: data.previous
  //     }));
  // }

  componentDidMount(){
    this.getStarshipsData();
  }

  render() {
    return (
      <div className="App">
        <StarshipsList 
          starships={this.state.starships}
        />
        {/* <button onClick={this.getPreviousPage(this.state.previous)} id='previous'>Previous</button> */}
        {/* <button onClick={this.getNextPage(this.state.next)} id='next'>Next</button> */}
      </div>
    );
  }
}

export default App
