import React from 'react';
import './App.scss';
import StarshipsList from '../StarshipsList/StarshipsList';

const ENDPOINT = 'https://swapi.co/api';

class App extends React.Component {
  constructor(props){
    super(props);
    this.state = {
        currentURL: ENDPOINT + '/starships/?page=1',
        starships: [],
        next: '',
        previous: '',
    }
  }

  componentDidMount(){
    this.getStarshipsData(this.state.currentURL);
  }

  getStarshipsData(currentURL) {
    fetch(currentURL)
      .then(data => data.json())
      .then(data => this.setState({
        currentURL: data,
        starships: data.results,
        next: data.next,
        previous: data.previous
      }));
  }

  handlePaginationButtonClick(e) {
    const previousButton = document.getElementById('previous');

    e.target === previousButton ? 
      this.getPreviousPage(this.state.previous) : 
      this.getNextPage(this.state.next)
  }

  getPreviousPage(previousURL) {
    console.log(previousURL)
    // this.getStarshipsData(previousURL);
  }

  getNextPage(nextURL) {
    console.log(nextURL)
    // this.getStarshipsData(nextURL);
  }

  render() {
    return (
      <div className="App">
        <StarshipsList 
          starships={this.state.starships}
        />
        <button onClick={this.handlePaginationButtonClick} id='previous'>Previous</button>
        <button onClick={this.handlePaginationButtonClick} id='next'>Next</button>
      </div>
    );
  }
}

export default App
